int random_wait(void)
{
    int n;
    int i = 0;
    int key = ERR;

    srand(time(NULL));
    n = rand() % 10;
    initscr();
    nodelay(stdscr, TRUE);
    while (i < n) {
        key = getch();
        if (key != ERR)
            return key;
        sleep(1);
        i++;
    }
    endwin();
    return key;
}

